import { Provider, TransactionRequest } from "@ethersproject/providers";
import { Contract, ContractFactory, Overrides } from "@ethersproject/contracts";

import { TestnetErc20 } from "./TestnetErc20ContractInterface";

export class TestnetErc20InstanceCreator extends ContractFactory {
  constructor(signer?: Signer) {
    super(_abi, _bytecode, signer);
  }

  deploy(
    _name: string,
    _symbol: string,
    _decimals: BigNumberish,
    overrides?: Overrides
  ): Promise<TestnetErc20> {
    return super.deploy(_name, _symbol, _decimals, overrides || {}) as Promise<
      TestnetErc20
    >;
  }
  getDeployTransaction(
    _name: string,
    _symbol: string,
    _decimals: BigNumberish,
    overrides?: Overrides
  ): TransactionRequest {
    return super.getDeployTransaction(
      _name,
      _symbol,
      _decimals,
      overrides || {}
    );
  }
  attach(address: string): TestnetErc20 {
    return super.attach(address) as TestnetErc20;
  }
  connect(signer: Signer): TestnetErc20InstanceCreator {
    return super.connect(signer) as TestnetErc20InstanceCreator;
  }
  static connect(
    address: string,
    signerOrProvider: Signer | Provider
  ): TestnetErc20 {
    return new Contract(address, _abi, signerOrProvider) as TestnetErc20;
  }
}

